def add_new_errors():
    """Add new errors to the dataset"""
    datasets_path = Path('Datasets')
    
    # Create a new training file for these entries
    new_df = pd.DataFrame(new_errors)
    
    # Save to a new file
    output_file = datasets_path / 'IT22002792_NewErrors_training.csv'
    new_df.to_csv(output_file, index=False, encoding='utf-8')
    
    sys.stdout.buffer.write(f"[OK] Created new dataset file: {output_file}\n".encode('utf-8'))
    sys.stdout.buffer.write(f"[OK] Added {len(new_errors)} new error entries\n".encode('utf-8'))
    print("\nNew entries:")
    for err in new_errors:
        print(f"  - {err['error_name']} ({err['category']})")
    
    return output_file

if __name__ == '__main__':
    try:
        output_file = add_new_errors()
        sys.stdout.buffer.write(f"\n[OK] Success! New dataset saved to: {output_file}\n".encode('utf-8'))
        print("\nNext steps:")
        print("1. The new dataset will be automatically loaded when you retrain the model")
        print("2. Run: python ml_backend/train_model.py")
    except Exception as e:
        print(f"Error: {e}")
        import traceback
        traceback.print_exc()
        sys.exit(1)

